function MowgliUI.SetScaleformParams(scaleform, data)
    data = data or {}
    for k, v in pairs(data) do
        PushScaleformMovieFunction(scaleform, v.name)
        if v.param then
            for _, par in pairs(v.param) do
                if math.type(par) == "integer" then
                    PushScaleformMovieFunctionParameterInt(par)
                elseif type(par) == "boolean" then
                    PushScaleformMovieFunctionParameterBool(par)
                elseif math.type(par) == "float" then
                    PushScaleformMovieFunctionParameterFloat(par)
                elseif type(par) == "string" then
                    PushScaleformMovieFunctionParameterString(par)
                end
            end
        end
        if v.func then
            v.func()
        end
        PopScaleformMovieFunctionVoid()
    end
end

---Visible
---@param Menu function
---@param Value boolean
---@return table
---@public
function MowgliUI.Visible(Menu, Value)
    if Menu ~= nil then
        if Menu() then
            if type(Value) == "boolean" then
                if Value then
                    if MowgliUI.CurrentMenu ~= nil then
                        MowgliUI.CurrentMenu.Closed()
                        MowgliUI.CurrentMenu.Open = not Value
                    end
                    Menu:UpdateInstructionalButtons(Value);
                    Menu:UpdateCursorStyle();
                    MowgliUI.CurrentMenu = Menu
                    menuOpen = true
                else
                    menuOpen = false
                    MowgliUI.CurrentMenu = nil
                end
                Menu.Open = Value
                MowgliUI.Options = 0
                MowgliUI.ItemOffset = 0
                MowgliUI.LastControl = false
            else
                return Menu.Open
            end
        end
    end
end 

function MowgliUI.CloseAll()
    menuOpen = false

    if MowgliUI.CurrentMenu ~= nil then
        local parent = MowgliUI.CurrentMenu.Parent
        while parent ~= nil do
            parent.Index = 1
            parent.Pagination.Minimum = 1
            parent.Pagination.Maximum = 10
            parent = parent.Parent
        end
        MowgliUI.CurrentMenu.Index = 1
        MowgliUI.CurrentMenu.Pagination.Minimum = 1
        MowgliUI.CurrentMenu.Pagination.Maximum = 10
        MowgliUI.CurrentMenu.Open = false
        MowgliUI.CurrentMenu = nil
    end
    MowgliUI.Options = 0
    MowgliUI.ItemOffset = 0
    ResetScriptGfxAlign()
end

