---Subtitle
---@return nil
---@public
function MowgliUI.Subtitle()
    if MowgliUI.CurrentMenu ~= nil then
        if MowgliUI.CurrentMenu() then
            MowgliUI.ItemsSafeZone(MowgliUI.CurrentMenu)
            if MowgliUI.CurrentMenu.Subtitle ~= "" then
                RenderRectangle(MowgliUI.CurrentMenu.X, MowgliUI.CurrentMenu.Y + MowgliUI.ItemOffset, MowgliUI.Settings.Items.Subtitle.Background.Width + MowgliUI.CurrentMenu.WidthOffset, MowgliUI.Settings.Items.Subtitle.Background.Height + MowgliUI.CurrentMenu.SubtitleHeight, 0, 0, 0, 255)
                RenderText(MowgliUI.CurrentMenu.Subtitle, MowgliUI.CurrentMenu.X + MowgliUI.Settings.Items.Subtitle.Text.X, MowgliUI.CurrentMenu.Y + MowgliUI.Settings.Items.Subtitle.Text.Y + MowgliUI.ItemOffset, 0, MowgliUI.Settings.Items.Subtitle.Text.Scale, 245, 245, 245, 255, nil, false, false, MowgliUI.Settings.Items.Subtitle.Background.Width + MowgliUI.CurrentMenu.WidthOffset)
                if MowgliUI.CurrentMenu.Index > MowgliUI.CurrentMenu.Options or MowgliUI.CurrentMenu.Index < 0 then
                    MowgliUI.CurrentMenu.Index = 1
                end
                MowgliUI.RefreshPagination()
                if MowgliUI.CurrentMenu.PageCounter == nil then
                    RenderText(MowgliUI.CurrentMenu.PageCounterColour .. MowgliUI.CurrentMenu.Index .. " / " .. MowgliUI.CurrentMenu.Options, MowgliUI.CurrentMenu.X + MowgliUI.Settings.Items.Subtitle.PreText.X + MowgliUI.CurrentMenu.WidthOffset, MowgliUI.CurrentMenu.Y + MowgliUI.Settings.Items.Subtitle.PreText.Y + MowgliUI.ItemOffset, 0, MowgliUI.Settings.Items.Subtitle.PreText.Scale, 245, 245, 245, 255, 2)
                else
                    RenderText(MowgliUI.CurrentMenu.PageCounter, MowgliUI.CurrentMenu.X + MowgliUI.Settings.Items.Subtitle.PreText.X + MowgliUI.CurrentMenu.WidthOffset, MowgliUI.CurrentMenu.Y + MowgliUI.Settings.Items.Subtitle.PreText.Y + MowgliUI.ItemOffset, 0, MowgliUI.Settings.Items.Subtitle.PreText.Scale, 245, 245, 245, 255, 2)
                end
                MowgliUI.ItemOffset = MowgliUI.ItemOffset + MowgliUI.Settings.Items.Subtitle.Background.Height
            end
        end
    end
end

