---Render
---@param instructionalButton boolean
---@return nil
---@public
function MowgliUI.Render(instructionalButton)
    if MowgliUI.CurrentMenu ~= nil then
        if MowgliUI.CurrentMenu() then
            if MowgliUI.CurrentMenu.Safezone then
                ResetScriptGfxAlign()
            end
            if (instructionalButton) then
                DrawScaleformMovieFullscreen(MowgliUI.CurrentMenu.InstructionalScaleform, 255, 255, 255, 255, 0)
            end
            MowgliUI.CurrentMenu.Options = MowgliUI.Options
            MowgliUI.CurrentMenu.SafeZoneSize = nil
            MowgliUI.Controls()
            MowgliUI.Options = 0
            MowgliUI.StatisticPanelCount = 0
            MowgliUI.ItemOffset = 0
            if MowgliUI.CurrentMenu.Controls.Back.Enabled and MowgliUI.CurrentMenu.Closable then
                if MowgliUI.CurrentMenu.Controls.Back.Pressed then
                    MowgliUI.CurrentMenu.Controls.Back.Pressed = false
                    local Audio = MowgliUI.Settings.Audio
                    MowgliUI.PlaySound(Audio[Audio.Use].Back.audioName, Audio[Audio.Use].Back.audioRef)
                    if MowgliUI.CurrentMenu.Closed ~= nil then
                        collectgarbage()
                        MowgliUI.CurrentMenu.Closed()
                    end
                    if MowgliUI.CurrentMenu.Parent ~= nil then
                        if MowgliUI.CurrentMenu.Parent() then
                            MowgliUI.NextMenu = MowgliUI.CurrentMenu.Parent
                            MowgliUI.CurrentMenu:UpdateCursorStyle()
                        else
                            --print('xxx') Debug print
                            MowgliUI.NextMenu = nil
                            MowgliUI.Visible(MowgliUI.CurrentMenu, false)
                        end
                    else
                        --print('zz') Debug print
                        MowgliUI.NextMenu = nil
                        MowgliUI.Visible(MowgliUI.CurrentMenu, false)
                    end
                end
            end
            if MowgliUI.NextMenu ~= nil then
                if MowgliUI.NextMenu() then
                    MowgliUI.Visible(MowgliUI.CurrentMenu, false)
                    MowgliUI.Visible(MowgliUI.NextMenu, true)
                    MowgliUI.CurrentMenu.Controls.Select.Active = false
                    MowgliUI.NextMenu = nil
                    MowgliUI.LastControl = false
                end
            end
        end
    end
end

---ItemsDescription
---@param CurrentMenu table
---@param Description string
---@param Selected boolean
---@return nil
---@public
function MowgliUI.ItemsDescription(CurrentMenu, Description, Selected)
    ---@type table
    if Description ~= "" or Description ~= nil then
        local SettingsDescription = MowgliUI.Settings.Items.Description;
        if Selected and CurrentMenu.Description ~= Description then
            CurrentMenu.Description = Description or nil
            ---@type number
            local DescriptionLineCount = GetLineCount(CurrentMenu.Description, CurrentMenu.X + SettingsDescription.Text.X, CurrentMenu.Y + SettingsDescription.Text.Y + CurrentMenu.SubtitleHeight + MowgliUI.ItemOffset, 0, SettingsDescription.Text.Scale, 255, 255, 255, 255, nil, false, false, SettingsDescription.Background.Width + (CurrentMenu.WidthOffset - 5.0))
            if DescriptionLineCount > 1 then
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height * DescriptionLineCount
            else
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height + 7
            end
        end
    end
end

---MouseBounds
---@param CurrentMenu table
---@param Selected boolean
---@param Option number
---@param SettingsButton table
---@return boolean
---@public
function MowgliUI.ItemsMouseBounds(CurrentMenu, Selected, Option, SettingsButton)
    ---@type boolean
    local Hovered = false
    Hovered = MowgliUI.IsMouseInBounds(CurrentMenu.X + CurrentMenu.SafeZoneSize.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SafeZoneSize.Y + CurrentMenu.SubtitleHeight + MowgliUI.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height)
    if Hovered and not Selected then
        RenderRectangle(CurrentMenu.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SubtitleHeight + MowgliUI.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height, 255, 255, 255, 20)
        if CurrentMenu.Controls.Click.Active then
            CurrentMenu.Index = Option
            local Audio = MowgliUI.Settings.Audio
            MowgliUI.PlaySound(Audio[Audio.Use].Error.audioName, Audio[Audio.Use].Error.audioRef)
        end
    end
    return Hovered;
end

---ItemsSafeZone
---@param CurrentMenu table
---@return nil
---@public
function MowgliUI.ItemsSafeZone(CurrentMenu)
    if not CurrentMenu.SafeZoneSize then
        CurrentMenu.SafeZoneSize = { X = 0, Y = 0 }
        if CurrentMenu.Safezone then
            CurrentMenu.SafeZoneSize = MowgliUI.GetSafeZoneBounds()
            SetScriptGfxAlign(76, 84)
            SetScriptGfxAlignParams(0, 0, 0, 0)
        end
    end
end

function MowgliUI.CurrentIsEqualTo(Current, To, Style, DefaultStyle)
    if (Current == To) then
        return Style;
    else
        return DefaultStyle or {};
    end
end

function MowgliUI.RefreshPagination()
    if (MowgliUI.CurrentMenu ~= nil) then
        if (MowgliUI.CurrentMenu.Index > 10) then
            local offset = MowgliUI.CurrentMenu.Index - 10
            MowgliUI.CurrentMenu.Pagination.Minimum = 1 + offset
            MowgliUI.CurrentMenu.Pagination.Maximum = 10 + offset
        else
            MowgliUI.CurrentMenu.Pagination.Minimum = 1
            MowgliUI.CurrentMenu.Pagination.Maximum = 10
        end
    end
end

function MowgliUI.IsVisible(menu, header, glare, instructional, items, panels)
    if (MowgliUI.Visible(menu)) then
        if (header == true) then
            MowgliUI.Banner(true, glare or false)
        end
        MowgliUI.Subtitle()
        if (items ~= nil) then
            items()
        end
        MowgliUI.Background();
        MowgliUI.Navigation();
        MowgliUI.Description();
        if (panels ~= nil) then
            panels()
        end
        MowgliUI.Render(instructional or false)
    end
end


---CreateWhile
---@param wait number
---@param menu table
---@param key number
---@param closure function
---@return void
---@public
function MowgliUI.CreateWhile(wait, menu, key, closure)
    Citizen.CreateThread(function()
        while (true) do
            Citizen.Wait(wait or 0.1)

            if (key ~= nil) then
                if IsControlJustPressed(1, key) then
                    MowgliUI.Visible(menu, not MowgliUI.Visible(menu))
                end
            end

            closure()
        end
    end)
end

---SetStyleAudio
---@param StyleAudio string
---@return void
---@public
function MowgliUI.SetStyleAudio(StyleAudio)
    MowgliUI.Settings.Audio.Use = StyleAudio or "MowgliUI"
end

function MowgliUI.GetStyleAudio()
    return MowgliUI.Settings.Audio.Use or "MowgliUI"
end

